package net.zooth;

import android.content.Intent;
import android.content.SharedPreferences;
import android.support.v7.app.AppCompatActivity;
import android.os.Bundle;
import android.text.method.LinkMovementMethod;
import android.widget.ImageView;
import android.widget.TextView;

public class History extends AppCompatActivity {

    private int y;
    private TextView his;
    private ImageView zoopic;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_history);

        Intent value = getIntent();
        y = value.getIntExtra("zooname", 1);

        zoopic = (ImageView) findViewById(R.id.zooimage);
        his = (TextView) findViewById(R.id.his);

        if(y==1){
            zoopic.setImageResource(R.drawable.dusitzoologo);
            his.setText("\t"+"สวนสัตว์ดุสิต เดิมเรียก สวนดุสิต หรือ เขาดินวนา พระบาทสมเด็จพระจุลจอมเกลาเจ้าอยู่หัวรัชกาลที่ 5 ทรงพระกรุณาโปรดเกล้าฯ ให้สร้างขึ้นตั้งแต่ปี พ.ศ. 2438 "+"\n"+"\n"+"\t"+"สวนสัตว์ดุสิตสร้างขึ้นในพื้นที่ราบทางด้านทิศตะวันออกติดคลองเปรมประชากร (ถนนพระราม 5) ด้วยการขุดสระนำ้ใหญ่ ประกอบคูคลองระบายนำ้ และถนน แล้วนำดินขึ้นมาเป็นเนินเขาเกาะกลางนำ้เรียกว่า วนา รวมอาณาเขต ส่วนนี้ทรงโปรดเรียกว่า เขาดินวนา โดยในขั้นต้นพระองค์ให้สร้างขึ้นสำหรับเป็นที่ประพาส ทรงพระเกษมสำราญส่วนประองค์ และข้าราชบริพารฝ่ายในก่อน ดังนั้นสวนดุสิต หรือ เขาดินวนา จึงเป็นส่วนหนึ่งในพระราชฐานพระราชวังดุสิต"+"\n"+"\n"+"\t"+"จากนั้นทางเทศบาลยครกรุงเทพ ได้จัดการย้ายกวางดาว และสัตว์ชนิดต่างๆ มาจากสวนอัมพรและย้ายสัตว์บางชนิด เช่น จระเข้ ลิง จากสวนสราญรมย์ มาไว้ที่เขาดินวนา และได้ขอให้ทางสำนักพระราชวังส่งช้างหลวงมาให้ประชาชนได้ชมในวันอาทิตย์ และเมื่อปรับปรุงสิ่งก่อสร้างต่างๆเรียบร้อยแล้ว ทางเทศบาลนครกรุงเทพจึงเปิดสวนดุสิตให้ประชาชนเที่ยวชม และพักผ่อนหย่อนใจได้เมื่อวันที่ ๑๘ มีนาคม พ.ศ. ๒๔๘๑ และให้เรียกสวนดุสิตนี้ว่า สวนสัตว์ดุสิต ซึ่งถือได้ว่าเป็นสวนสัตว์แห่งแรกในประเทศไทย\n");
        }else if (y==2){
            zoopic.setImageResource(R.drawable.khaokeaw);
            his.setText("\t"+"สวนสัตว์เปิดเขาเขียว เปิดให้บริการ เมื่อวันที่ ๑ มิถุนายน ๒๕๒๑ หนึ่งในสวนสัตว์ชั้นนำทั่วโลก ตั้งอยู่เลขที่ ๒๓๕ หมู่ที่ ๗ ตำบลบางพระ อำเภอศรีราชา จังหวัดชลบุรี เป็นสวนสัตว์บริการสาธารณะ ซึ่งรัฐพึงดำเนินการโดยการก่อตั้งขององค์การสวนสัตว์ในพระบรมราชูปถัมภ์  "+"\n"+"\n"+"\t"+"ในปี พ.ศ. ๒๕๑๗สวนสัตว์เปิดเขาเขียว จังหวัดชลบุรี ถูกจัดตั้งขึ้น ซึ่งเริ่มแรกเป็นเพียงสถานที่พักฟื้นโดยมีการระบายสัตว์ที่อยู่ในสวนสัตว์ดุสิต ซึ่งมีพื้นที่น้อยแต่ปริมาณสัตว์มากเกินสมควร ส่งผลให้สัตว์หลายชนิดมีสภาพความเป็นอยู่ที่แออัดและการขยายพันธุ์สัตว์เป็นไปอย่างเชื่องช้า อีกทั้งสัตว์ป่าที่หายากบางชนิดไม่มีการขยายพันธุ์ จึงได้ทำการระบายสัตว์ต่าง ๆ มาเลี้ยงดูให้อยู่ในสภาพที่ใกล้เคียงธรรมชาติ บนพื้นที่ ๕๐๐ ไร่"+"\n"+"\n"+"\t"+"ในเวลาต่อมา องค์การสวนสัตว์ได้เริ่มดำเนินการปรับปรุงสถานที่ฟื้นฟูสภาพป่าก่อสร้างอาคารต่างๆ  คณะรัฐมนตรีได้มีมติเห็นชอบให้มีการจัดทำโครงการพัฒนาสวนสัตว์เปิดเขาเขียวเพื่ออำนวยประโยชน์ให้แก่สังคมอย่างกว้างขวางเพิ่มมากขึ้นด้วยงบประมาณของโครงการระยะที่ ๑ เป็นจำนวนทั้งสิ้น ๕๘๙.๐๖๒ ล้านบาท พร้อมกันนี้ได้รับการขยายพื้นที่เพิ่มขึ้นอีก ๒,๐๐๐ ไร่ จนกระทั่งปัจจุบันนี้สวนสัตว์เปิดเขาเขียวมีพื้นที่ทั้งหมดเป็นจำนวนถึง ๕,๐๐๐ ไร่\n");
        }else if (y==3){
            zoopic.setImageResource(R.drawable.chiangmai);
            his.setText("\t"+"สวนสัตว์เชียงใหม่ ก่อตั้งขึ้นโดยนาย ฮาโรลด์ เมสัน ยัง (Mr. Harold Mason Young) มิชชั่นนารีชาวอเมริกัน ผู้เข้ามาเป็นอาสาสมัครสอนการยังชีพในป่าให้แก่พวกทหารและตำรวจชายแดน ในช่วงสงครามเกาหลี ( พ.ศ.๒๔๙๓-๒๔๙๖ ) โดยอาศัยพื้นที่บ้านที่ตนเช่าอยู่คือ บ้านเวฬุวัน เชิงดอยสุเทพ เป็นสถานที่เริ่มต้นโดยเริ่มเปิดเป็นสวนสัตว์เล็กๆ ของเอกชนขึ้นเป็นครั้งแรกเมื่อประมาณ พ.ศ.๒๔๙๕  เหตุผลของการสะสมสัตว์ชนิดต่างๆ ของนาย ฮาโรลด์ เมสัน ยัง จนสามารถจัดเป็นสวนสัตว์เอกชนขึ้นได้นั้น แม้ไม่ปรากฏหลักฐานชัด แต่คงเนื่องด้วยความรักเมตตาต่อสัตว์เป็นพื้นฐาน และด้วยประโยชน์ในการประกอบอาชีพเป็นอาสาสมัครสอนการยังชีพในป่าให้แก่ทหาร และตำรวจชายแดน ซึ่งต้องผจญกับสัตว์ป่านานาชนิดเสมอ ต่อมาจึงติดต่อขอที่ดินป่าสงวน เชิงดอยสุเทพ ต่อผู้ว่าราชการจังหวัดเชียงใหม่โดยตรง จนได้รับอนุมัติให้ใช้พื้นที่ป่าสงวนเชิงดอยสุเทพ ประมาณ ๖๐ ไร่ เป็นที่ตั้งสวนสัตว์ของเอกชน เปิดบริการให้เข้าชม ตั้งแต่วันจักรี ๖ เมษายน พ.ศ.๒๕๐๐"+"\n"+"\n"+"\t"+"จนกระทั่งนาย ฮาโรลด์ เมสัน ยัง ถึงแก่อนิจกรรม ใน พ.ศ.๒๕๑๘ องค์การบริหารส่วนจังหวัดเชียงใหม่ ได้พิจารณาเห็นคุณค่าของสวนสัตว์ของนาย ฮาโรลด์ เมสัน ยัง ทั้งในฐานะเป็นแหล่ง พักผ่อนศึกษาสัตว์ของประชาชนในจังหวัดเชียงใหม่ และเป็นแหล่งดึงดูดนักท่องเที่ยวแหล่งหนึ่งตามโครงการปรับปรุงดอยสุเทพ เป็นสถานที่พักผ่อนหย่อนใจ จึงรับกิจการสวนสัตว์ของนาย ฮาโรลด์ เมสัน ยัง ไว้ในความดูแลขององค์การบริหารส่วนจังหวัดเชียงใหม่"+"\n"+"\n"+"\t"+"ในปี พ.ศ.๒๕๒๐ สวนสัตว์เชียงใหม่จึงโอนเข้าสังกัดองค์การสวนสัตว์แห่งประเทศไทยสำนักนายกรัฐมนตรีตั้งแต่ วันที่ ๑๖ มิถุนายน ๒๕๒๐ เป็นต้นมา และเมื่อสวนสัตว์เชียงใหม่มีอายุครบ ๑๐ ปี ได้ขยายพื้นที่จากเดิมประมาณ ๖๐ ไร่ ได้ขยายเป็น ๑๓๐ ไร่ ตั้งแต่ปี พ.ศ.๒๕๒๖ สวนสัตว์เชียงใหม่ก็ได้รับความเห็นชอบจากกรมป่าไม้ กระทรวงเกษตรและสหกรณ์ และคณะรัฐมนตรีให้ขยายพื้นที่บริเวณเชิงดอยสุเทพ เพิ่มเติมอีกประมาณ ๕๐๐ ไร่ จนปัจจุบันสวนสัตว์เชียงใหม่มีพื้นที่ ๕๓๑ ไร่ และอยู่ภายใต้การกำกับดูแลของ “องค์การสวนสัตว์ ในพระบรมราชูปถัมภ์ กระทรวงทรัพยากรธรรมชาติและสิ่งแวดล้อม”\n");
        }else if (y==4){
            zoopic.setImageResource(R.drawable.korat);
            his.setText("\t"+"สวนสัตว์นครราชสีมา สวนสัตว์ลำดับที่ ๔ ในองค์การสวนสัตว์ในพระบรมราชูปถัมภ์ สังกัดกระทรวงทรัพยากรธรรมชาติและสิ่งแวดล้อม จัดตั้งขึ้นตามมติคณะรัฐมนตรี เมื่อวันที่ ๒๓ พฤษภาคม ๒๕๓๒ ภายใต้ชื่อ “โครงการสวนสัตว์นครราชสีมา” จนกระทั่งมีพิธีเปิดอย่างเป็นทางการ เมื่อวันที่ ๑๔ ธันวาคม ๒๕๓๙ โดยมี พลเอกเปรม ติณสูลานนท์ ประธานองคมนตรีและรัฐบุรุษ เป็นประธานในพิธี และใช้ชื่อว่า “สวนสัตว์นครราชสีมา” เป็นต้นมา "+"\n"+"\n"+"\t"+"สวนสัตว์นครราชสีมา ...ซาฟารีอีสาน... ด้วยพื้นที่ทั้งหมดประมาณ ๕๔๕ ไร่ มีการแสดงสัตว์ป่านานาชนิด อยู่ในความดูแลทั้งหมดประมาณ ๑,๘๐๐ ตัว เนื่องจากสภาพภูมิอากาศอำนวย จึงมีการปรับพื้นที่เสมือนทุ่งหญ้าสะวันนา ซึ่งเป็นการเชื่อมโยงระหว่างสภาพทางภูมิศาสตร์และการจัดแสดงสัตว์ป่าที่เหมาะสมตามหลักภูมิศาสตร์ การนำสัตว์จากแอฟริกามาจัดแสดงได้แก่ “The Big Five” ๕ สัตว์ผู้ยิ่งใหญ่แห่งทุ่งหญ้าแอฟริกาได้แก่ สิงโต เสือดาว-เสือดำ ช้างแอฟริกา แรดขาว ควายป่าแอฟริกา รวมทั้งสัตว์ป่าจากทวีปแอฟริกาอื่นๆ เช่นยีราฟ ม้าลาย แอนติโลป เป็นต้น ");
        }else if (y==5){
            zoopic.setImageResource(R.drawable.songkla);
            his.setText("\t"+"หลังการเลือกตั้ง เมื่อวันที่ ๒ กรกฎาคม ๒๕๒๙ พลเอกเปรม ติณสูลานนท์ ได้รับโปรดเกล้าฯ ให้ดำรงตำแหน่งนายกรัฐมนตรี หลังจากนั้น นายอำนวย สุวรรณคีรี ได้รับแต่งตั้งให้เป็นรัฐมนตรีประจำสำนักนายกรัฐมนตรี รับผิดชอบควบคุมดูแล องค์การสวนสัตว์ ได้มีนโยบายจัดตั้งสวนสัตว์ทั่วทุกภาค สำหรับภาคใต้ได้คัดเลือกสถานที่ ที่จังหวัดสงขลา บริเวณเขารูปช้าง ตำบลเขารูปช้าง อำเภอเมือง จังหวัดสงขลา (บ้านสวนตูล)"+"\n"+"\n"+"โครงการสวนสัตว์สงขลา เป็นโครงการ ๑๐ ปี โดยระยะแรกรัฐบาลให้เงินอุดหนุน ๑๒๐ ล้านบาท โดยเริ่มดำเนินการตั้งแต่ปี ๒๕๓๒–๒๕๔๑ มีสิ่งก่อสร้างได้ดำเนินการไปแล้ว คือ สาธารณูปโภคพื้นฐาน อาคารสำนักงาน อ่างเก็บน้ำ เรือนเพาะชำ และสถานที่จัดแสดงสัตว์ป่าชนิดต่างๆ ซึ่งปัจจุบันได้ทยอย นำสัตว์ชนิดต่างๆเข้ามาแสดงให้ประชาชนชาวสงขลาและจังหวัดใกล้เคียงได้ชม เช่น สมเสร็จ กระจง อูฐ ลามา ยีราฟ หมี เสือ ลิงชะนี กวาง ละมั่ง เก้ง นกสวยงาม เช่น นกแก้ว นกมาร์คอว์ นกคลาสโซวารี นกฟลามิงโก และสัตว์อื่นอีกมากมาย"+"\n"+"\n"+"สวนสัตว์สงขลา เป็นรัฐวิสาหกิจ สังกัดกระทรวงทรัพยากรธรรมชาติและสิ่งแวดล้อม ซึ่งเป็นสวนสัตว์แห่งที่ ๕ ภายใต้การกำกับดูแลขององค์การสวนสัตว์ ในพระบรมราชูปถัมภ์ และเป็นสวนสัตว์แห่งแรกของภาคใต้โดยคณะรัฐมนตรีมีมติให้จัดตั้งขึ้น เมื่อวันที่ ๒๓ พฤษภาคม พ.ศ.๒๕๓๒ ในพื้นที่ ๘๗๘ ไร่ เริ่มเก็บค่าบัตรผ่านประตูตั้งแต่วันที่ ๑๘ กันยายน ๒๕๔๑ เป็นต้นมาและทำพิธีเปิดอย่างเป็นทางการในวันที่ ๓ ตุลาคม ๒๕๔๑\n");
        }else if (y==6){
            zoopic.setImageResource(R.drawable.logo_ubonzoo);
            his.setText("\t"+"สวนสัตว์อุบลราชธานีตั้งอยู่ในพื้นที่เขตป่าสงวนแห่งชาติดงฟ้าห่วน ๑๑๒ หมู่ ๑๗ ตำบลขามใหญ่ อำเภอเมือง จังหวัดอุบลราชธานี มีพื้นที่จำนวน ๑,๒๑๗ ไร่ ในการดำเนินการได้กำหนดเรื่องราวความคิดที่เป็นจุดสำคัญ คือ Jungle Park คือการนำสวนสัตว์ (Zoo) เข้ามาผสมผสานกับความสมบูรณ์ของป่าไม้ภายในพื้นที่ โดยใช้การอนุรักษ์และหาประโยชน์จากสภาพผืนป่าแบบระมัดระวังและรอบคอบ ไม่ให้มีการเปลี่ยนแปลงสภาพพื้นที่ของป่า และลดการทำลายสภาพพื้นที่เดิมให้มากที่สุด ความต้องการของประชาชน และหน่วยงานทุกภาคส่วนในจังหวัด องค์ประกอบทางด้านเศรษฐกิจ สังคม วัฒนธรรม และอื่นๆ ซึ่งถือเป็นต้นทุนสำคัญคณะกรรมการองค์การ สวนสัตว์ มีมติเห็นชอบในหลักการที่จะดำเนินโครงการสวนสัตว์อุบลราชธานี โดยให้รัฐบาลสนับสนุนงบประมาณการดำเนินงานทั้งหมด"+"\n\n\t"+"องค์การสวนสัตว์ฯ ได้ประสานงานกับกรมป่าไม้ เพื่อขอใช้พื้นที่จัดทำสวนสัตว์จังหวัดอุบลราชธานีและกรมป่าไม้เห็นชอบให้องค์การสวนสัตว์เข้าทำประโยชน์ในพื้นที่ป่าสงวนแห่งชาติดงฟ้า ส่วนกรมป่าไม้อนุญาตให้องค์การสวนสัตว์ เข้าใช้พื้นที่ดำเนินการจัดตั้งสวนสัตว์อุบลราชธานีบนพื้นที่ ๑,๒๑๗ ไร่เป็นเวลานาน ๓๐ ปี โดยประกาศกรมป่าไม้ ฉบับที่ ๑๕/๒๕๔๐ ลงวันที่ ๒๑ สิงหาคม ๒๕๔๐\n");
        }else if (y==7){
            zoopic.setImageResource(R.drawable.khonkaen);
            his.setText("\t"+"สวนสัตว์ขอนแก่น หรือเรียกขานกันในนาม สวนสัตว์เขาสวนกวาง สังกัดองค์การสวนสัตว์ ได้ถือกำเนิดขึ้นในแดนดินถิ่นอีสาน ซึ่งองค์การสวนสัตว์ ได้รับนโยบายจากกระทรวงทรัพยากรธรรมชาติและสิ่งแวดล้อมให้สำรวจพื้นที่ก่อสร้างอุทยานสัตว์ป่าอีสานตอนบน ขอนแก่น-อุดรธานี บริเวณภูเขาสวนกวาง ในพื้นที่ป่าสงวนแห่งชาติเขาสวนกวาง อำเภอเขาสวนกวาง จังหวัดขอนแก่น คาบเกี่ยวกับพื้นที่ป่าสงวนแห่งชาติ อำเภอโนนสะอาด จังหวัดอุดรธานี รวมพื้นที่ ๓,๓๓๘ ไร่"+"\n\n\t"+"สวนสัตว์ขอนแก่น ได้เปิดอย่างไม่เป็นทางการ เมื่อวันที่ ๙ มกราคม ๒๕๕๓ ภายในพื้นที่ ได้มีการบริหารจัดการพื้นที่ออกเป็น ๒ ส่วน คือส่วนการศึกษา และส่วนจัดแสดงสัตว์ โดยพื้นที่ส่วนการศึกษา ให้เป็นแหล่งเรียนรู้และศึกษา ทางด้านการอนุรักษ์พันธุ์สัตว์ป่าหายาก เป็นศูนย์วิจัย เพาะเลี้ยง และเป็นการท่องเที่ยวแบบอัธยาศัย รวมทั้งกิจกรรมเสริมอื่นๆ ได้แก่การเดินศึกษาป่าธรรมชาติ โครงการนักเรียนเข้าเรียนรู้ในสวนสัตว์ โครงการแค้มป์ปิ้ง โครงการกิจกรรมค่ายลูกเสือและยุวกาชาด ส่วนที่ ๒ คือส่วนจัดแสดงสัตว์หรือ Zoo Zone เป็นแหล่งรวบรวมชนิดพันธุ์ของสัตว์ไว้หลากหลายเพื่อการเพาะและขยายพันธุ์ การวิจัยสัตว์ป่าที่หายากที่นำมาจัดแสดง อีกทั้งยังเป็นสถานที่พักผ่อนหย่อนใจเป็นแหล่งเที่ยวชมสัตว์ป่าของจังหวัดขอนแก่น จังหวัดอุดรธานีและใกล้เคียง เช่น ส่วนจัดแสดงกรงนกใหญ่นกแร้ง แรดขาว เสือขาว สิงโตขาว โดยเฉพาะสัตว์ในตระกูลกวาง เป็นการนำมาจัดแสดงในพื้นที่ที่กว้างขวางและรวมกันอยู่เป็นฝูงขนาดใหญ่ อยู่อย่างธรรมชาติให้สอดคล้องกับประวัติความเป็นมาของภูเขาแสนกวาง ซึ่งเคยมีกวางนานาสายพันธุ์มากมายนับแสนตัว ภายใต้เอกลักษณ์ของอุทยานสัตว์ป่าฯ “ LAND OF THE DEERS ”\n");
        }else if (y==8){
            zoopic.setImageResource(R.drawable.surin);
            his.setText("\t"+"โครงการคชอาณาจักร จังหวัดสุรินทร์ เป็นหน่วยงานในสังกัดองค์การสวนสัตว์ในพระบรมราชูปถัมภ์ กระทรวงทรัพยากรธรรมชาติและสิ่งแวดล้อม มีวัตถุประสงค์การก่อตั้งขึ้นเพื่อช่วยเหลือช้างเร่ร่อนที่เป็นปัญหาอยู่ใน เมืองใหญ่ในปัจจุบัน ควาญช้างนำช้างมาเดินเร่ขายอาหารช้าง จากสภาพความแออัดของเมืองหลวงทำให้ช้างเกิดการบาดเจ็บ และล้มตายจากอุบัติเหตุ บางครั้งเกิดความเครียด อาละวาดทำร้ายประชาชนให้เห็นอยู่บ่อยครั้ง"+"\n\n\t"+"แนวทางการดำเนินการโครงการคชอาณาจักร จังหวัดสุรินทร์จัดทำเป็นสถานที่ท่องเที่ยวรูปแบบอนุรักษ์ธรรมชาติ และวัฒนธรรมท้องถิ่น เน้นความเป็นอยู่บนพื้นฐานหมู่บ้านช้างเศรษฐกิจพอเพียง เพิ่มความสมบรูณ์ของพื้นที่ ความมั่งคั่งของอาหารช้าง เพื่อการพึ่งพาตนเองอย่างยั่งยืน โดยขอใช้พื้นที่ป่าสงวนแห่งชาติสวนป่าดงภูดิน อำเภอท่าตูม จังหวัดสุรินทร์ ประมาณ ๓,๐๐๐ ไร่ เป็นพื้นที่ดำเนินการ"+"\n\n\t"+"โครงการคชอาณาจักร จังหวัดสุรินทร์ ดำเนินการโดยองค์การสวนสัตว์ในพระบรมราชูปถัมภ์ตามนโยบายกระทรวงทรัพยากรธรรมชาติและสิ่งแวดล้อม มีวัตถุประสงค์เพื่อช่วยแก่ปัญหาช้างเร่ร่อนให้กลับคืนถิ่น ณ หมู่บ้านช้าง บ้านตากลาง ตำบลกระโพ อำเภอท่าตูม จังหวัดสุรินทร์ มีเป้าหมายรับช้างเร่ร่อน ๒๐๐ เชือก นำเข้าบริหารจัดการด้านการท่องเที่ยวเชิงวัฒนธรรมผสมผสานกับการชมธรรมชาติของช้าง "+"\n\n\t"+"การนำช้างมาเลี้ยงรวมกันปริมาณมากย่อมก่อให้เกิดปัญหาตามมา โดยเฉพาะด้านของเสียที่เกิดจากมูลช้าง ซึ่งมีปริมาณมากเฉลี่ยประมาณ ๕๐ กิโลกรัมต่อเชือก หรือราวๆ ๕-๑๐ ตันต่อวัน หากทิ้งไว้ในพื้นที่หรือจัดเก็บไม่หมดก็อาจทำให้เกิดปัญหาสภาพแวดล้อมส่งผลกระทบต่อสุขภาพของช้างได้ ปัจจุบันปางช้างหลายแห่งได้มีการพัฒนาแปรรูปมูลช้างไปเป็นกระดาษมูลสาช้าง ผลิตเป็นสินค้าที่ระลึกจำหน่ายสร้างรายได้มากมาย นอกจากนี้ยังมีการนำมูลช้างไปผลิตเป็นก๊าซชีวภาพ พลังงานทดแทนที่มีค่ามหาศาลได้ โรงงานแปรรูปและพลังงานทดแทนจะเป็นโครงการที่สร้างมูลค่าเพิ่มให้อย่างมาก และยังเป็นประโยชน์ในด้านการให้การศึกษาแก่นักเรียน เยาวชน และนักท่องเที่ยวที่สนใจอีกด้วย \n");
        }


    }
}
